const simpleCode = `
<ToastProvider>
<ToastConsumer>
  {({ addToast, removeToast }) => (
    <Button
      onClick={
        () => addToast(
          <Dismiss
            onClose={removeToast}
          >
          Lorem Ipsum is not gypsum. Did you know Toast
          </Dismiss>,
        )
        }
    >
    Show Toast
    </Button>
  )}
</ToastConsumer>
</ToastProvider>
`;
