import React from 'react';
import { GroupToggle, Button } from 'reactify';
import {
  onSelectionCode,
  simpleCode,
} from './code-refrence';
import './group-toggle.scss';
import DemoCard from '../../components/demo-card';

const GroupToggleDemo = () => (
  <div>
    <DemoCard
      title="Simple Use"
      uniqueIdentifier="simpleUse"
      sourceCode={simpleCode}
    >
      <GroupToggle>
        {(selectedIndex, onClick) => (
          <div>
            <Button index={0} theme={selectedIndex === 0 ? 'default' : 'success'} onClick={() => onClick(0)} text="Option 1" />
            <Button index={1} theme={selectedIndex === 1 ? 'default' : 'success'} onClick={() => onClick(1)} text="Option 1" />
            <Button index={2} theme={selectedIndex === 2 ? 'default' : 'success'} onClick={() => onClick(2)} text="Option 1" />
          </div>
        )}
      </GroupToggle>
    </DemoCard>

    <DemoCard
      title="onSelection"
      uniqueIdentifier="onSelectionUse"
      sourceCode={onSelectionCode}
    >
      <GroupToggle onSelectionChange={index => alert(`Selected index ${index}`)}>
        {({ selected, onClick }) => <Button index={0} theme={selected ? 'default' : 'success'} onClick={onClick} text="Option 1" />}
        {({ selected, onClick }) => <Button index={1} theme={selected ? 'default' : 'success'} onClick={onClick} text="Option 2" />}
        {({ selected, onClick }) => <Button index={2} theme={selected ? 'default' : 'success'} onClick={onClick} text="Option 3" />}
      </GroupToggle>
    </DemoCard>
  </div>
);

export default GroupToggleDemo;
