import React from 'react';
import {
  Button, Dismiss, ToastProvider, ToastConsumer,
} from '@../../../../reactify/build';

const ToastDemo = () => (
  <div>
    <ToastProvider>
      <ToastConsumer>
        {({ add }) => (
          <Button onClick={() => add(<Dismiss>Simple Toast</Dismiss>)}>
            Toasty
          </Button>
        )}
      </ToastConsumer>
    </ToastProvider>
  </div>
);

export default ToastDemo;
