import React from 'react';
import { GroupToggle, Button } from 'reactify';
import {
  onSelectionCode,
  simpleCode,
} from './code-refrence';
import './group-toggle.scss';
import DemoCard from '../../components/demo-card';

const GroupToggleDemo = () => (
  <div>
    <DemoCard
      title="Simple Use"
      uniqueIdentifier="simpleUse"
      sourceCode={simpleCode}
    >
      <GroupToggle>
        {({ selected, onClick }) => <Button theme={selected ? 'default' : 'success'} onClick={onClick} text="Option 1" />}
        {({ selected, onClick }) => <Button theme={selected ? 'default' : 'success'} onClick={onClick} text="Option 2" />}
        {({ selected, onClick }) => <Button theme={selected ? 'default' : 'success'} onClick={onClick} text="Option 3" />}
      </GroupToggle>
    </DemoCard>

    <DemoCard
      title="onSelection"
      uniqueIdentifier="onSelectionUse"
      sourceCode={onSelectionCode}
    >
      <GroupToggle onSelection={index => alert(`Selected index ${index}`)}>
        {({ selected, onClick }) => <Button theme={selected ? 'default' : 'success'} onClick={onClick} text="Option 1" />}
        {({ selected, onClick }) => <Button theme={selected ? 'default' : 'success'} onClick={onClick} text="Option 2" />}
        {({ selected, onClick }) => <Button theme={selected ? 'default' : 'success'} onClick={onClick} text="Option 3" />}
      </GroupToggle>
    </DemoCard>
  </div>
);

export default GroupToggleDemo;
