import React, { Component } from 'react';
import { Login, Button } from '@../../../../reactify/build';
import {
  simpleCode,
} from './code-refrence';
import './login.scss';
import DemoCard from '../../components/demo-card';

class ModalDemo extends Component {
    state = {
      config: {
        fields: {
          username: {
            value: 'username',
            isError: true,
            errorText: 'username error',
            isLoading: false,
            disabled: false,
          },
          password: {
            value: 'password',
            isError: true,
            errorText: 'password error',
            isLoading: false,
            disabled: false,
          },
          submit: {
            isLoading: false,
            disabled: false,
          },
          error: {
            visible: true,
            text: 'main error',
          },
        },
      },
    };

    handleLogin = ({ action, value, prevState }) => {
      if (action === Login.actionTypes.username) {
        this.setState({ config: { fields: { username: { value } } } });
      } else if (action === Login.actionTypes.password) {
        this.setState({ config: { fields: { password: { value } } } });
      } else if (action === Login.actionTypes.submit) {
        this.setState(
          {
            config: {
              fields: {
                username: {
                  isLoading: true,
                  disabled: true,
                },
                password: { isLoading: true, disabled: true },
                submit: { isLoading: true, disabled: true },
              },
            },
          },
        );
      }
    }

    render() {
      return (
        <div>
          <DemoCard
            title="Simple Use"
            uniqueIdentifier="simpleUse"
            sourceCode={simpleCode}
          >
            <Login state={this.state.config} updateState={this.handleLogin} />
          </DemoCard>
        </div>
      );
    }
}

export default ModalDemo;
