const customColorCode = `
<Text style={{ color: 'violet' }}>A custom color text</Text>
`;

const customClassCode = `
<Text className="bg-dark text-light">A custom class text</Text>
`;

const customThemeCode = `
<Text primary>A primary text</Text>
<Text secondary>A secondary text</Text>
<Text dark>A dark text</Text>
<Text light>A light text</Text>
<Text info>A info text</Text>
<Text warning>A warning text</Text>
<Text danger>A danger text</Text>
<Text success>A success text</Text>
`;

const closeProgramaticallyCode = `
<UserProfile
  imgPath="https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"
  userName="Joe"
>
{closeTray => (
  <button
    className="btn btn-dark p-5"
    onClick={() => closeTray}
    type="button"
  >
Close Tray
  </button>
)}
</UserProfile>
`;

const simpleCode = `
<NumberCounter
  from={1000}
  to={9876}
/>
`;

const sizeCode = `
<NumberCounter
  from={this.state.simpleCounter.from}
  to={this.state.simpleCounter.to}
  small
/>

<NumberCounter
  from={this.state.simpleCounter.from}
  to={this.state.simpleCounter.to}
  medium
/>

<NumberCounter
  from={this.state.simpleCounter.from}
  to={this.state.simpleCounter.to}
  large
/>

<NumberCounter
  from={this.state.simpleCounter.from}
  to={this.state.simpleCounter.to}
  style={{ fontSize: '75px' }}
/>
`;


export {
  customColorCode,
  customClassCode,
  customThemeCode,
  simpleCode,
  sizeCode,
};
