import React, { Component } from 'react';
import { Login, Button } from '@../../../../reactify/build';
import {
  simpleCode,
} from './code-refrence';
import './login.scss';
import DemoCard from '../../components/demo-card';

class ModalDemo extends Component {
    state = {
      config: {
        fields: {
          username: {
            value: 'username',
            isError: true,
            errorText: 'username error',
            isLoading: true,
            disabled: true,
          },
          password: {
            value: 'password',
            isError: true,
            errorText: 'password error',
            isLoading: true,
            disabled: true,
          },
          submit: {
            isLoading: true,
            disabled: true,
          },
          error: {
            visible: true,
            text: 'main error',
          },
        },
      },
      injectConfig: false,
    };

    handleLogin = ({ action, value, state }) => {
      if (action === Login.actionTypes.username) {
        this.setState({ config: { fields: { username: { value } } } });
      } else if (action === Login.actionTypes.password) {
        this.setState({ config: { fields: { password: { value } } } });
      } else if (action === Login.actionTypes.submit) {
        this.setState({ config: { fields: { submit: { isLoading: true, disabled: true } } } });
      }
    }

    render() {
      return (
        <div>
          <DemoCard
            title="Simple Use"
            uniqueIdentifier="simpleUse"
            sourceCode={simpleCode}
          >
            <Login state={this.state.injectConfig ? this.state.config : undefined} updateState={this.handleLogin} />
            <Button onClick={() => this.setState({ injectConfig: true })}>Inject Config</Button>
          </DemoCard>
        </div>
      );
    }
}

export default ModalDemo;
