import React from 'react';
import { GroupToggle, Button } from 'reactify';
import {
  onSelectionCode,
  simpleCode,
  disabledCode,
} from './code-refrence';
import './group-toggle.scss';
import DemoCard from '../../components/demo-card';

const GroupToggleDemo = () => (
  <div>
    <DemoCard
      title="Simple Use"
      uniqueIdentifier="simpleUse"
      sourceCode={simpleCode}
    >
      <GroupToggle>
        {({ selectedIndex }) => (
          <>
            <Button
              index={0}
              success={selectedIndex === 0}
              default={selectedIndex !== 0}
            >
              Option 1
            </Button>
            <Button
              index={1}
              success={selectedIndex === 1}
              default={selectedIndex !== 1}
            >
              Option 2
            </Button>
            <Button
              index={2}
              success={selectedIndex === 2}
              default={selectedIndex !== 2}
            >
Option 3

            </Button>
          </>
        )}
      </GroupToggle>
    </DemoCard>

    <DemoCard
      title="onSelection"
      uniqueIdentifier="onSelectionUse"
      sourceCode={onSelectionCode}
    >
      <GroupToggle onSelectionChange={index => alert(`Selected index ${index}`)}>
        {({ selectedIndex }) => (
          <>
            <Button index={0} success={selectedIndex === 0} default={selectedIndex !== 0}>Option 1</Button>
            <Button index={1} success={selectedIndex === 1} default={selectedIndex !== 1}>Option 2</Button>
            <Button index={2} success={selectedIndex === 2} default={selectedIndex !== 2}>Option 3</Button>
          </>
        )}
      </GroupToggle>
    </DemoCard>

    <DemoCard
      title="Disabled"
      uniqueIdentifier="diabledUse"
      sourceCode={disabledCode}
    >
      <GroupToggle disabled>
        {({ selectedIndex }) => (
          <>
            <Button index={0} success={selectedIndex === 0} default={selectedIndex !== 0}>Option 1</Button>
            <Button index={1} success={selectedIndex === 1} default={selectedIndex !== 1}>Option 2</Button>
            <Button index={2} success={selectedIndex === 2} default={selectedIndex !== 2}>Option 3</Button>
          </>
        )}
      </GroupToggle>
    </DemoCard>
  </div>
);

export default GroupToggleDemo;
