import React, { Component } from 'react';
import { Portal, Button } from '@../../../../reactify/build';
import {
  autoCloseCode,
  hideBackdropCode,
  simpleCode,
  portalLocationCode,
} from './code-refrence';
import './portal.scss';
import DemoCard from '../../components/demo-card';

class PortalDemo extends Component {
    state = {
      simplePortalOpen: false,
      leftPortalOpen: false,
      rightPortalOpen: false,
      topPortalOpen: false,
      bottomPortalOpen: false,
      autoClosePortalOpen: false,
      hideBackdropPortalOpen: false,
      onClosePortal: false,
    };

    render() {
      return (
        <div>
          <DemoCard
            title="Simple Use"
            uniqueIdentifier="simpleUse"
            sourceCode={simpleCode}
          >
            <Button onClick={() => this.setState({ simplePortalOpen: true })}>Open Portal</Button>
            {this.state.simplePortalOpen && (
            <Portal top onClose={() => this.setState({ simplePortalOpen: false })}>
              {closeToggle => (
                <div
                  style={{
                    width: '100%',
                    height: '100px',
                    display: 'flex',
                    justifyContent: 'center',
                    alignContent: 'center',
                    alignItems: 'center',
                    background: 'white',
                  }}
                >
                  <input
                    style={{ fontSize: '20px' }}
                    type="button"
                    value="Close"
                    onClick={closeToggle}
                  />
                </div>
              )}
            </Portal>
            )}
          </DemoCard>

          <DemoCard
            title="Portal Location"
            uniqueIdentifier="portalLocationUse"
            sourceCode={portalLocationCode}
          >
            <Button onClick={() => this.setState({ topPortalOpen: true })}>Open Top Portal</Button>
            {this.state.topPortalOpen && (
            <Portal top onClose={() => this.setState({ topPortalOpen: false })}>
              {closeToggle => (
                <div
                  style={{
                    width: '100%',
                    height: '100px',
                    display: 'flex',
                    justifyContent: 'center',
                    alignContent: 'center',
                    alignItems: 'center',
                    background: 'white',
                  }}
                >
                  <input
                    style={{ fontSize: '20px' }}
                    type="button"
                    value="Close"
                    onClick={closeToggle}
                  />
                </div>
              )}
            </Portal>
            )}
            <br />
            <br />
            <Button
              onClick={() => this.setState({ leftPortalOpen: true })}
            >
                Open Left Portal
            </Button>
            {this.state.leftPortalOpen && (
            <Portal left onClose={() => this.setState({ leftPortalOpen: false })}>
              {closeToggle => (
                <div
                  style={{
                    width: '100px',
                    height: '100%',
                    display: 'flex',
                    justifyContent: 'center',
                    alignContent: 'center',
                    alignItems: 'center',
                    background: 'white',
                  }}
                >
                  <input
                    style={{ fontSize: '20px' }}
                    type="button"
                    value="Close"
                    onClick={closeToggle}
                  />
                </div>
              )}
            </Portal>
            )}
            <br />
            <br />
            <Button
              onClick={() => this.setState({ rightPortalOpen: true })}
            >
                Open Right Portal
            </Button>
            {this.state.rightPortalOpen && (
            <Portal right onClose={() => this.setState({ rightPortalOpen: false })}>
              {closeToggle => (
                <div
                  style={{
                    width: '100px',
                    height: '100%',
                    display: 'flex',
                    justifyContent: 'center',
                    alignContent: 'center',
                    alignItems: 'center',
                    background: 'white',
                  }}
                >
                  <input
                    style={{ fontSize: '20px' }}
                    type="button"
                    value="Close"
                    onClick={closeToggle}
                  />
                </div>
              )}
            </Portal>
            )}
            <br />
            <br />
            <Button
              onClick={() => this.setState({ bottomPortalOpen: true })}
            >
                Open Bottom Portal
            </Button>
            {this.state.bottomPortalOpen && (
            <Portal bottom onClose={() => this.setState({ bottomPortalOpen: false })}>
              {closeToggle => (
                <div
                  style={{
                    width: '100%',
                    height: '100px',
                    display: 'flex',
                    justifyContent: 'center',
                    alignContent: 'center',
                    alignItems: 'center',
                    background: 'white',
                  }}
                >
                  <input
                    style={{ fontSize: '20px' }}
                    type="button"
                    value="Close"
                    onClick={closeToggle}
                  />
                </div>
              )}
            </Portal>
            )}
            <br />
          </DemoCard>

          <DemoCard
            title="Auto Close"
            uniqueIdentifier="autoCloseUse"
            sourceCode={autoCloseCode}
          >
            <h6 className="pb-3">
              By default the portal closes when clicking outside the portal
               but that can be disabled by passing
              {' '}
              {'autoClose={false}'}
            </h6>
            <Button
              onClick={() => this.setState({ autoClosePortalOpen: true })}
            >
                Open Portal
            </Button>
            {this.state.autoClosePortalOpen && (
            <Portal top autoClose={false}>
              {closeToggle => (
                <div
                  style={{
                    width: '100%',
                    height: '100px',
                    display: 'flex',
                    justifyContent: 'center',
                    alignContent: 'center',
                    alignItems: 'center',
                    background: 'white',
                  }}
                >
                  <input
                    style={{ fontSize: '20px' }}
                    type="button"
                    value="Close"
                    onClick={() => { this.setState({ autoClosePortalOpen: false }); closeToggle(); }}
                  />
                </div>
              )}
            </Portal>
            )}
          </DemoCard>

          <DemoCard
            title="Hide Backdrop"
            uniqueIdentifier="hideBackdropUse"
            sourceCode={hideBackdropCode}
          >
            <h6 className="pb-3">
                To make the backdrop opaque
            </h6>
            <Button
              onClick={() => this.setState({ hideBackdropPortalOpen: true })}
            >
                Open Portal
            </Button>
            {this.state.hideBackdropPortalOpen && (
            <Portal top hideBackDrop onClose={() => this.setState({ hideBackdropPortalOpen: false })}>
              {closeToggle => (
                <div
                  style={{
                    width: '100%',
                    height: '100px',
                    display: 'flex',
                    justifyContent: 'center',
                    alignContent: 'center',
                    alignItems: 'center',
                    background: 'white',
                  }}
                >
                  <input
                    style={{ fontSize: '20px' }}
                    type="button"
                    value="Close"
                    onClick={closeToggle}
                  />
                </div>
              )}
            </Portal>
            )}
          </DemoCard>

          <DemoCard
            title="onClose Event"
            uniqueIdentifier="onCloseUse"
            sourceCode={onCloseCode}
          >
            <h6 className="pb-3">
                To make the backdrop opaque
            </h6>
            <Button
              onClick={() => this.setState({ hideBackdropPortalOpen: true })}
            >
                Open Portal
            </Button>
            {this.state.hideBackdropPortalOpen && (
            <Portal top hideBackDrop onClose={() => this.setState({ hideBackdropPortalOpen: false })}>
              {closeToggle => (
                <div
                  style={{
                    width: '100%',
                    height: '100px',
                    display: 'flex',
                    justifyContent: 'center',
                    alignContent: 'center',
                    alignItems: 'center',
                    background: 'white',
                  }}
                >
                  <input
                    style={{ fontSize: '20px' }}
                    type="button"
                    value="Close"
                    onClick={closeToggle}
                  />
                </div>
              )}
            </Portal>
            )}
          </DemoCard>
        </div>
      );
    }
}

export default PortalDemo;
