const customColorCode = `
<NumberCounter
  from={this.state.simpleCounter.from}
  to={this.state.simpleCounter.to}
  style={{ color: 'violet' }}
/>
`;

const customClassCode = `
<NumberCounter
  from={this.state.simpleCounter.from}
  to={this.state.simpleCounter.to}
  className="bg-dark p-3"
/>
`;

const customThemeCode = `
<NumberCounter
  from={this.state.simpleCounter.from}
  to={this.state.simpleCounter.to}
  primary
/>
<NumberCounter
  from={this.state.simpleCounter.from}
  to={this.state.simpleCounter.to}
  secondary
/>

<NumberCounter
  from={this.state.simpleCounter.from}
  to={this.state.simpleCounter.to}
  dark
/>

<NumberCounter
  from={this.state.simpleCounter.from}
  to={this.state.simpleCounter.to}
  light
/>

<NumberCounter
  from={this.state.simpleCounter.from}
  to={this.state.simpleCounter.to}
  info
/>

<NumberCounter
  from={this.state.simpleCounter.from}
  to={this.state.simpleCounter.to}
  warning
/>

<NumberCounter
  from={this.state.simpleCounter.from}
  to={this.state.simpleCounter.to}
  danger
/>

<NumberCounter
  from={this.state.simpleCounter.from}
  to={this.state.simpleCounter.to}
  success
/>
`;

const simpleCode = `
<Spinner
  bars
/>
`;

const sizeCode = `
<NumberCounter
from={this.state.simpleCounter.from}
to={this.state.simpleCounter.to}
small
/>

<NumberCounter
from={this.state.simpleCounter.from}
to={this.state.simpleCounter.to}
medium
/>

<NumberCounter
from={this.state.simpleCounter.from}
to={this.state.simpleCounter.to}
large
/>

<h6 className="pt-3">style={{ fontSize: '75px' }}</h6>
<NumberCounter
from={this.state.simpleCounter.from}
to={this.state.simpleCounter.to}
style={{ fontSize: '75px' }}
/>
`;


export {
  customColorCode,
  customClassCode,
  customThemeCode,
  simpleCode,
  sizeCode,
};
