import React from 'react';
import { Tooltip } from '@../../../../reactify/build';
import {
  positionCode,
  simpleCode,
} from './code-refrence';
import './tooltip.scss';
import DemoCard from '../../components/demo-card';

const TooltipDemo = () => (
  <div>
    <DemoCard
      title="Simple Use"
      uniqueIdentifier="simpleUse"
      sourceCode={simpleCode}
    >
      <Tooltip>Hover over me to show tooltip</Tooltip>
    </DemoCard>

    <DemoCard
      title="Positioning"
      uniqueIdentifier="positioningUse"
      sourceCode={positionCode}
    >
      <Tooltip top content="I am a tooltip">Hover over me to show top tooltip</Tooltip>
      <Tooltip left>Hover over me to show left tooltip</Tooltip>
      <Tooltip right>Hover over me to show right tooltip</Tooltip>
      <Tooltip bottom>Hover over me to show bottom tooltip</Tooltip>
    </DemoCard>
  </div>
);

export default TooltipDemo;
