/* eslint-disable react/no-unescaped-entities */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React from 'react';
import {
  Paper,
  TreeView,
  TreeViewItem,
} from '@../../../../reactify/build';
import {
  customDeclarativeCode,
  customThemeCode,
  simpleCode,
} from './code-refrence';
import './tree-view.scss';
import DemoCard from '../../components/demo-card';

const items = [
  {
    text: 'Option 1', index: 0,
  },
  {
    text: 'Option 2',
    index: 1,
    sub: [
      {
        text: 'Sub Option 1',
        index: 2,
        sub: [
          {
            text: 'Sub Option 1',
            index: 3,
            sub: [
              {
                text: 'Sub Option 1',
                index: 4,
                sub: [
                  {
                    text: 'Sub Option 1',
                    index: 5,
                    sub: [
                      {
                        text: 'Sub Option 1',
                        index: 6,
                        sub: [
                          {
                            text: 'Sub Option 1',
                            index: 7,
                            sub: [
                              { text: 'Sub Option 1', index: 8 },
                              { text: 'Sub Option 2', index: 9 },
                              { text: 'Sub Option 3', index: 10 },
                              { text: 'Sub Option 4', index: 11 },
                            ],
                          },
                          { text: 'Sub Option 2', index: 12 },
                          { text: 'Sub Option 3', index: 13 },
                          { text: 'Sub Option 4', index: 14 },
                        ],
                      },
                      { text: 'Sub Option 2', index: 15 },
                      { text: 'Sub Option 3', index: 16 },
                      { text: 'Sub Option 4', index: 17 },
                    ],
                  },
                  { text: 'Sub Option 2', index: 18 },
                  { text: 'Sub Option 3', index: 19 },
                  { text: 'Sub Option 4', index: 20 },
                ],
              },
              { text: 'Sub Option 2', index: 21 },
              { text: 'Sub Option 3', index: 22 },
              { text: 'Sub Option 4', index: 23 },
            ],
          },
          { text: 'Sub Option 2', index: 24 },
          { text: 'Sub Option 3', index: 25 },
          { text: 'Sub Option 4', index: 26 },
        ],
      },
      { text: 'Sub Option 2', index: 27 },
      { text: 'Sub Option 3', index: 28 },
      { text: 'Sub Option 4', index: 29 },
    ],
  },
  { text: 'Option 2', index: 30 },
  { text: 'Option 4', index: 31 },
];

const TreeViewDemo = () => (
  <>
    <DemoCard
      title="Simple Use"
      uniqueIdentifier="simpleUse"
      sourceCode={simpleCode}
    >
      <TreeView items={items} />
    </DemoCard>

    <DemoCard
      title="Themes Use"
      uniqueIdentifier="themesUse"
      sourceCode={customThemeCode}
    >
      <h5>Theming is only for the default icons</h5>
      <h5>Primary Theme</h5>
      <TreeView items={items} />
      <h5>Secondary Theme</h5>
      <TreeView items={items} secondary />
      <h5>Info Theme</h5>
      <TreeView items={items} info />
      <h5>Light Theme</h5>
      <TreeView items={items} light />
      <h5>Warning Theme</h5>
      <TreeView items={items} warning />
      <h5>Danger Theme</h5>
      <TreeView items={items} danger />
      <h5>Success Theme</h5>
      <TreeView items={items} success />
    </DemoCard>

    <DemoCard
      title="Custom Content (Declarative)"
      uniqueIdentifier="customDeclarativeUse"
      sourceCode={customDeclarativeCode}
    >
      <h5>Declared Menu</h5>
      <TreeView>
        <TreeViewItem text="Option 1">
          <TreeView>
            <TreeViewItem text="Option 1" />
            <TreeViewItem text="Option 2" />
          </TreeView>
        </TreeViewItem>
        <TreeViewItem text="Option 2" />
      </TreeView>

      <h5 className="mt-5">Declared Menu with custom content</h5>
      <TreeView>
        <TreeViewItem
          content={
            ({ isTreeOpen, toggleSubTree }) => (
              <Paper
                onClick={toggleSubTree}
                className="w-100"
                danger={isTreeOpen}
                success={!isTreeOpen}
              >
                {isTreeOpen ? 'Close Sub Tree' : 'Open Sub Tree'}
              </Paper>
            )}
        >
          <TreeView>
            <TreeViewItem
              text="Sub Option 1"
            />
            <TreeViewItem
              text="Sub Option 2"
            />
          </TreeView>
        </TreeViewItem>
        <TreeViewItem text="Option 2" />
      </TreeView>
    </DemoCard>
  </>
);

export default TreeViewDemo;
