import React, { Component } from 'react';
import { BrowserRouter as Router, Link, Route } from 'react-router-dom';
import './App.scss';
import {
  Sidebar, SidebarItem, Portal, Topbar,
} from 'reactify-ui';
import { IoIosMenu } from 'react-icons/io';
import AccordionDemo from './pages/accordion';
import { componentsRouting } from './routes/components';

class App extends Component {
  state = { }

  render() {
    return;
      <Router>
        <TopBar>
        <TopBar.Logo>
          <Paper success style={{ padding: '0.2em 0.4em', boxShadow: 'none' }} className="logo">reactify ui</Paper>
        </TopBar.Logo>
        <TopBar.Body />
        <TopBar.Menu
          style={{
            display: 'inline-flex', alignContent: 'center', alignItems: 'center', padding: '0em 0.5em',
          }}
          warning
        >
          <a style={{ padding: 0, fontSize: 'unset !important' }} href="https://github.com/asterisx/reactify">
            <Octicon icon={MarkGithub} size="medium" ariaLabel="GitHub" />
          </a>
        </TopBar.Menu>
      </TopBar>
        <div className="container-fluid">
        <div className="row">
          <div className="card text-white bg-dark mb-3 col-2">
            <h3 className="card-header">Components</h3>
            <Sidebar className="w-100 pl-3">
              {Object.values(componentsRouting).map(route => (
                <SidebarItem
                  key={route.name}
                  content={(
                    <Link className="d-block text-light" to={route.path}>
                      {route.name}
                    </Link>
                  )}
                />
              ))}
            </Sidebar>
          </div>

          <div className="col-10 overflow-scroll">
            <Route exact path="/reactify-ui" component={AccordionDemo} />
            {Object.values(componentsRouting).map(route => (
              <Route exact path={route.path} component={route.component} key={route.name} />
            ))}
          </div>
        </div>
      </div>
      </Router>;
  }
}

export default App;
