/* eslint-disable react/no-unescaped-entities */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React from 'react';
import {
  Breadcrumbs,
} from '@../../../../reactify/build';
import { FaAngleRight } from 'react-icons/fa';
import {
  declarativeCode,
  seperatorCode,
  simpleCode,
} from './code-refrence';
import './breadcrumbs.scss';
import DemoCard from '../../components/demo-card';

const items = [
  {
    content: 'Home',
    key: 0,
    onClick: () => console.log('Home'),
  },
  {
    content: 'About',
    key: 1,
    onClick: () => console.log('About'),
  },
  {
    content: 'Company Culture',
    key: 2,
    onClick: () => console.log('Company Culture'),
  },
];

const BreadcrumbsDemo = () => (
  <>
    <DemoCard
      title="Simple Use"
      uniqueIdentifier="seperatorUse"
      sourceCode={simpleCode}
    >
      <h6>Check the console for events</h6>
      {/* <Breadcrumbs items={items} /> */}
    </DemoCard>

    <DemoCard
      title="Seperator"
      uniqueIdentifier="seperatorUse"
      sourceCode={seperatorCode}
    >
      {/* <Breadcrumbs items={items} seperator={<FaAngleRight />} /> */}
    </DemoCard>

    <DemoCard
      title="Declarative"
      uniqueIdentifier="customDeclarativeUse"
      sourceCode={declarativeCode}
    >
      <h6>
        Breadcrumbs.Item is a 'li' under the hood.
        You can thus pass anything to Breadcrumbs.Item as children and any props to it too.
        Whatever you pass will be passed down to the underlying 'li' as is.
      </h6>
      <Breadcrumbs>
        <Breadcrumbs.Item index={0} style={{ color: 'blue' }} onClick={() => console.log('Home')}>
          Home
        </Breadcrumbs.Item>
        <Breadcrumbs.Item index={1} style={{ color: 'blue' }} onClick={() => console.log('About')}>
          About
        </Breadcrumbs.Item>
        <Breadcrumbs.Item key={2} onClick={() => console.log('Company Culture')}>
          Company Culture
        </Breadcrumbs.Item>
      </Breadcrumbs>
    </DemoCard>
  </>
);

export default BreadcrumbsDemo;
