import React from 'react';
import {
  Button, Dismiss, ToastProvider, ToastConsumer,
} from '@../../../../reactify/build';

const ToastDemo = () => (
  <div>
    <ToastProvider topLeft pauseOnHover>
      <ToastConsumer>
        {({ addToast, removeToast }) => (
          <Button onClick={() => addToast(<Dismiss onClose={removeToast}>Lorem Ipsum is not gypsum. Did you know Toast</Dismiss>)}>
            Toasty
          </Button>
        )}
      </ToastConsumer>
    </ToastProvider>

    <ToastProvider topCenter>
      <ToastConsumer>
        {({ addToast }) => (
          <Button onClick={() => addToast(<Dismiss>Lorem Ipsum is not gypsum. Did you know Toast</Dismiss>)}>
            Toasty
          </Button>
        )}
      </ToastConsumer>
    </ToastProvider>

    <ToastProvider topRight>
      <ToastConsumer>
        {({ addToast }) => (
          <Button onClick={() => addToast(<Dismiss>Simple Toast</Dismiss>)}>
            Toasty
          </Button>
        )}
      </ToastConsumer>
    </ToastProvider>

    <ToastProvider bottomLeft>
      <ToastConsumer>
        {({ addToast }) => (
          <Button onClick={() => addToast(<Dismiss>Simple Toast</Dismiss>)}>
            Toasty
          </Button>
        )}
      </ToastConsumer>
    </ToastProvider>

    <ToastProvider bottomCenter>
      <ToastConsumer>
        {({ addToast }) => (
          <Button onClick={() => addToast(<Dismiss>Simple Toast</Dismiss>)}>
            Toasty
          </Button>
        )}
      </ToastConsumer>
    </ToastProvider>

    <ToastProvider bottomRight>
      <ToastConsumer>
        {({ addToast }) => (
          <Button onClick={() => addToast(<Dismiss>Simple Toast</Dismiss>)}>
            Toasty
          </Button>
        )}
      </ToastConsumer>
    </ToastProvider>


  </div>
);

export default ToastDemo;
