import React, { Component } from 'react';
import {
  Button,
  Timer,
} from '@../../../../reactify/build';
import {
  simpleCode,
} from './code-refrence';
import './timer.scss';
import DemoCard from '../../components/demo-card';

const millisOfFuture = Date.now() + (1000 * 5);
const millisOfPast = Date.now() - (1000 * 5);
const millisOfSomeDistantFuture = Date.now() - (1000 * 5 * 30);

class TimerDemo extends Component {
  state = {
    showDays: true,
    showHours: true,
    showMinutes: true,
    showSeconds: true,
  }

  toggle = (prop) => {
    if (prop === 'showDays') { this.setState(prevState => ({ showDays: !prevState.showDays })); }
  }

  render() {
    return (
      <div>
        <DemoCard
          title="Simple Use"
          uniqueIdentifier="simpleUse"
          sourceCode={simpleCode}
        >
          <Timer milliseconds={millisOfFuture} />
          <br />
          <Timer milliseconds={millisOfPast} />
        </DemoCard>

        <DemoCard
          title="Props"
          uniqueIdentifier="propsUse"
          sourceCode={simpleCode}
        >
          <Timer
            milliseconds={millisOfSomeDistantFuture}
            showDays={this.state.showDays}
            showHours={this.state.showHours}
            showMinutes={this.state.showMinutes}
            showSeconds={this.state.showSeconds}
          />
          <br />
          <br />
          <Button
            onClick={() => this.toggle('showDays')}
          >
            Toggle Days
          </Button>
        </DemoCard>
      </div>
    );
  }
}

export default TimerDemo;
