const simpleCode = `
<Button onClick={() => this.setState({ simpleModalOpen: true })}>Open Modal</Button>
{this.state.simpleModalOpen && (
<Modal onClose={() => this.setState({ simpleModalOpen: false })}>
  {closeToggle => (
    <div
      style={{
        width: '100%',
        height: '250px',
        display: 'flex',
        justifyContent: 'center',
        alignContent: 'center',
        alignItems: 'center',
        background: 'white',
      }}
    >
      <input
        style={{ fontSize: '20px' }}
        type="button"
        value="Close"
        onClick={closeToggle}
      />
    </div>
  )}
</Modal>
)}
`;


export {
  simpleCode,
};
